import React, { Component } from "react";
import Navbar from "./navbar";
import axios from "axios";
import CreatableSelect from "react-select/creatable";

class Profile extends Component {
  state = {
    user: { ...this.props.user },
    weight: "",
    level: ""
  };

  handleChange = e => {
    this.setState({
      [e.target.name]: e.target.value
    });
  };
  handleChange3 = (newValue: any, actionMeta: any) => {
    console.group("Value Changed");
    console.log(newValue);
    console.log(`action: ${actionMeta.action}`);
    console.groupEnd();
    this.setState({
      level: newValue.value
    });
  };
  handleInputChange = (inputValue: any, actionMeta: any) => {
    console.group("Input Changed");
    console.log(inputValue);
    console.log(`action: ${actionMeta.action}`);
    console.groupEnd();
  };
  getInfo = async e => {
    e.preventDefault();
    console.log(this.state.user.height);

    let h2 = this.state.user.height;

    let res1 = await axios({
      method: "GET",
      url: "https://fitness-calculator.p.rapidapi.com/dailycalory",
      headers: {
        "content-type": "application/octet-stream",
        "x-rapidapi-host": "fitness-calculator.p.rapidapi.com",
        "x-rapidapi-key": "e14d7b4a61mshaf4d68517150093p1d2b11jsnaa5e4d29c6bc",
        useQueryString: true
      },
      params: {
        heigth: this.state.user.height,
        age: this.state.user.birth,
        gender: this.state.user.gender,
        weigth: this.state.weight
      }
    });
    console.log("RES", res1.data["Sedentary: little or no exercise"]);
    let levels = [
      { value: "female", label: "Sedentary" },
      { value: "male", label: "Lightly active" },
      { value: "male", label: "Moderately active" },
      { value: "male", label: "Very active" },
      { value: "male", label: "Extra active" }
    ];
    this.setState({});
  };
  render() {
    return (
      <div>
        <Navbar />
        <div className="form-group">
          <form onSubmit={this.getInfo}>
            <label>Your weight</label>
            <input
              id="typeinp"
              type="range"
              min="20"
              max="150"
              name="weight"
              onChange={this.handleChange}
              step="1"
            />
            {this.state.weight} kg
            <div className="form-group">
              <input type="submit" value="Submit" className="btn btn-primary" />
            </div>
          </form>
          <h2>Choose your Activity Level</h2>
          {/* <CreatableSelect
            isClearable
            onChange={this.handleChange3}
            onInputChange={this.handleInputChange}
            options={levels}
          /> */}
        </div>
      </div>
    );
  }
}

export default Profile;
